import { useEffect, useRef, useState } from "react";

const Login = (props) => {
    const [signIn, setSignIn] = useState(false);

    const button = useRef(null);

    useEffect(() => {
        const inputs = document.querySelectorAll('input');

        inputs.forEach(input => {
            if (props.isDark) {
                input.classList.add('bg-black', 'border-white', 'shadow-white');
                input.classList.remove('bg-white', 'border-black', 'shadow-black');
            } else {
               input.classList.remove('bg-black', 'border-white', 'shadow-white');
                input.classList.add('bg-white', 'border-black', 'shadow-black'); 
            }
        });

        if (props.isDark) {
            button.current.classList.add('bg-gradient-to-r', 'from-white', 'via-black', 'to-white');
            button.current.classList.remove('bg-gradient-to-r', 'from-fuchsia-500', 'via-white', 'to-fuchsia-600');
        }
    }, [props.isDark]);

    return (
        <div className="min-h-screen">
            <form className="flex flex-col shadow-lg justify-center align-center content-center">
                <label>Username</label>
                <input type="text" className="h-8 border border-2 border-solid border-black rounded-md shadow-sky-500" />
                <label>Email</label>
                <input type="email" className="h-8 border border-2 border-solid border-black rounded-md shadow-sky-500" />
                <label>Phone Number</label>
                <input type="tel" className="h-8 border border-2 border-solid border-black rounded-md shadow-sky-500" />
                <label>Password</label>
                <input type="password" className="h-8 border border-2 border-solid border-black rounded-md shadow-sky-500" />
                <label>Confirm Password</label>
                <input type="password" className="h-8 border border-2 border-solid border-black rounded-md shadow-sky-500" />

                <button ref={button} type="submit" className="py-3 bg-gradient-to-r from-fuchsia-500 via-white to-fuchsia-600 border-none rounded-lg my-5 w-[350px] flex justify-center justify-self-center ">Sign up</button>
            </form>
        </div>
    );
};

export default Login;
