import './index.css';
import Navbar from './Components/Navbar';
import { useEffect, useRef, useState } from 'react';
import Home from './Home/Home';
import Login from './Login/Login';
import About from './About/About';
import Footer from './Components/Footer';

function App() {
  const [isDark, setIsDark] = useState(true);
  const [activePage, setActivePage] = useState('home');

  useEffect(() => {
    document.body.className = isDark ? 'bg-black text-white' : 'bg-white text-black';

    if (isDark) {
      footer.current.classList.add('border-white');
      footer.current.classList.remove('border-black');
    } else {
      footer.current.classList.remove('border-white');
      footer.current.classList.add('border-black');
    }
  }, [isDark]);

  const darkModeHandler = () => setIsDark(prev => !prev);

  return (
    <>
      <Navbar
        setActivePage={setActivePage}
        darkModeHandler={darkModeHandler}
        isDark={isDark}
      />

      {activePage === 'home' ? <Home isDark={isDark} /> : null}
      {activePage === 'login' ? <Login isDark={isDark} /> : null}
      {activePage === 'about' ? <About /> : null}

      {activePage === 'login' ? '' : (
        <>
          <div ref={footer} className="border-t-2 my-[500px] grid grid-cols-3 gap-2">
            <div>
              <p className='font-bold'>Documentation</p>
            </div>
            <div>
              <p className='font-bold'>About Us</p>
              <a className='block'>Who are we?</a>
              <a className='block'>Our insight</a>
              <a className='block'>Our Team</a>
            </div>
            <div>
              <p className='font-bold'>FAQ</p>
              <a className='block'>How to get the forecast of my state?</a>
            </div>
          </div>
        </>
      )}
    </>
  );
}

export default App;
