import { useEffect, useRef } from "react";
import { motion } from "framer-motion";

const Home = (props) => {
    const button = useRef(null);

    useEffect(() => {
        if (props.isDark) {
            button.current.classList.add('text-white');
            button.current.classList.remove('text-black');
        } else {
            button.current.classList.remove('text-white');
            button.current.classList.add('text-black');
        }
    } ,[props.isDark]);

    return (
        <>
            <motion.div
                initial={{opacity: 0, y: 50}} 
                animate={{opacity: 1, y: 0}} 
                transition={{duration: 2, ease: "easeOut"}}
            >
                <p className="pl-[200px] pt-[200px] font-bold text-3xl">The first end-to-end TEMPO support, <br />monitoring and forecasting air quality<br />in North America.</p>
                <p className="pl-[200px] text-xl">Resources from NASA and other TEMPO institutions, real-time, <br />user-friendly air quality forecast application and tips to be<br />protected form pollution.</p>
                <button ref={button} className='ml-[200px] mt-[50px] px-4 py-3 bg-purple-500 border-none rounded-full text-lg'>See latest forecasts</button>
            </motion.div>

            <motion.div
                initial={{opacity: 0, y: 50}} 
                animate={{opacity: 1, y: 0}} 
                transition={{duration: 2, ease: "easeOut"}}
                className="bg-[]"
            >
                <p className="pl-[200px] pt-[200px] font-bold text-3xl">We aim to a green life</p>
            </motion.div>
        </>
    );
};

export default Home;
