import { useEffect, useRef } from "react";

const Login = (props) => {
    const button = useRef(null);
    const form = useRef(null);

    useEffect(() => {
        const inputs = document.querySelectorAll('input');

        inputs.forEach(input => {
            if (props.isDark) {
                input.classList.add('bg-black', 'border-white', 'shadow-white');
                input.classList.remove('bg-white', 'border-black', 'shadow-black');
            } else {
               input.classList.remove('bg-black', 'border-white', 'shadow-white');
                input.classList.add('bg-white', 'border-black', 'shadow-black'); 
            }
        });

        if (props.isDark) {
            form.current.classList.add('shadow-white');
            form.current.classList.remove('shadow-black');

            button.current.classList.add('border-white');
            button.current.classList.remove('border-black');
        } else {
            form.current.classList.remove('shadow-white');
            form.current.classList.add('shadow-black');

            button.current.classList.remove('border-white');
            button.current.classList.add('border-black');
        }
    }, [props.isDark]);

    return (
        <div className="min-h-screen flex flex-col justify-center align-center m-auto w-[800px] h-[750px] ">
            <form ref={form} className="flex flex-col shadow-lg justify-center content-center border">
                <label>Username</label>
                <input type="text" className="h-8 border border-2 border-solid border-black rounded-md" />
                <label>Email</label>
                <input type="email" className="h-8 border border-2 border-solid border-black rounded-md" />
                <label>Phone Number</label>
                <input type="tel" className="h-8 border border-2 border-solid border-black rounded-md" />
                <label>Password</label>
                <input type="password" className="h-8 border border-2 border-solid border-black rounded-md" />
                <label>Confirm Password</label>
                <input type="password" className="h-8 border border-2 border-solid border-black rounded-md" />

                <button ref={button} type="submit" className="py-3 border border-2 rounded-lg my-5 mx-auto w-[350px] flex justify-center justify-self-center hover:bg-grey-700">Sign up</button>
            </form>
        </div>
    );
};

export default Login;
