import { useEffect, useRef } from "react";

const Home = (props) => {
    const button = useRef(null);

    useEffect(() => {
        if (props.isDark) {
            button.current.classList.add('bg-white', '');
        }
    } ,[props.isDark]);

    return (
        <div>
            <p className="pl-[200px] pt-[200px] font-bold text-3xl">The first end-to-end TEMPO support, <br />monitoring and forecasting air quality<br />in North America.</p>
            <p className="pl-[200px] text-xl">Resources from NASA and other TEMPO institutions, real-time<br />air quality forecast and tips to be protected form pollution.</p>
            <button ref={button} className="ml-[200px] mt-[50px] px-3 py-2 border-none rounded-full  text-lg">See latest forecasts</button>
        </div>
    );
};

export default Home;
