import logo from './logo.svg';
import './index.css';
import { useEffect, useState } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSun } from '@fortawesome/free-solid-svg-icons';

function App() {
  const [isDark, setIsDark] = useState(false);
  const [Signed, setSigned] = useState(false);

  const darkModeHandler = () => setIsDark(!isDark);

  useEffect(() => {
    document.body.className = isDark ? 'bg-black text-white' : 'bg-white text-black';
  }, [isDark]);

  return (
    <>
      <div className="flex flex-row bg-transparent py-3 px-2">


          <button className="relative " onClick={() => darkModeHandler()}>
              <FontAwesomeIcon icon={faSun} />
          </button>
      </div>
    </>
  );
}

export default App;
