import './index.css';
import Navbar from './Navbar';
import { useEffect, useRef, useState } from 'react';
import Home from './Home';
import Login from './Login';
import About from './About';

function App() {
  const [isDark, setIsDark] = useState(true);
  const [activePage, setActivePage] = useState('home');

  const footer = useRef(null);

  useEffect(() => {
    document.body.className = isDark ? 'bg-black text-white' : 'bg-white text-black';

    if (isDark) {
      footer.current.classList.add('border-white');
      footer.current.classList.remove('border-black');
    } else {
      footer.current.classList.remove('border-white');
      footer.current.classList.add('border-black');
    }

  }, [isDark]);

  const darkModeHandler = () => setIsDark(prev => !prev);

  return (
    <>
      <Navbar
        setActivePage={setActivePage}
        darkModeHandler={darkModeHandler}
        isDark={isDark}
      />

      {activePage === 'home' ? <Home /> : null}
      {activePage === 'login' ? <Login isDark={isDark} /> : null}
      {activePage === 'about' ? <About /> : null}

      {activePage === 'login' ? (
        <></>
      ) : ''}
    </>
  );
}

export default App;
