import logo from './logo.svg';
import './index.css';
import Navbar from './Navbar';
import { useEffect, useState } from 'react';
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faSun } from '@fortawesome/free-solid-svg-icons';

function App() {
  const [isDark, setIsDark] = useState(false);
  const [Signed, setSigned] = useState(false);

  const darkModeHandler = () => setIsDark(!isDark);

  useEffect(() => {
    document.body.className = isDark ? 'bg-black text-white' : 'bg-white text-black';
  }, [isDark]);

  return (
    <>
      <div className="flex flex-row bg-transparent py-3 px-2">
            <a className="pr-5">Home</a>
            <a className="pr-5">Log in</a>
            <a className="pr-5">About</a>

            <button className="relative " onClick={props.handler}>
                <FontAwesomeIcon icon={faSun} />
            </button>
        </div>
      <Navbar handler={darkModeHandler} />
    </>
  );
}

export default App;
