import { useEffect, useRef } from "react";
import { Link } from "react-router-dom";

const Footer = (props) => {
    const footer = useRef(null);

    useEffect(() => {
        if (props.isDark) {
            footer.current.classList.add('border-gray-600', 'bg-slate-900');
            footer.current.classList.remove('border-slate-200', 'bg-slate-100');
        } else {
            footer.current.classList.remove('border-gray-600', 'bg-slate-900');
            footer.current.classList.add('border-slate-200', 'bg-slate-100');
        }
    }, [props.isDark]);

    return (
        <div ref={footer} className="border-t-2 bg-slate-900 grid grid-cols-3 gap-2 p-5">
            <div>
                <p className='font-bold'>Documentation</p>
                <a href="#1" className='block'>Getting started</a>
            </div>
            <div>
                <p className='font-bold'>About Us</p>
                <a to="/#who" className='block'>Who are we?</a>
                <a to="/#insight" className='block'>Our insight</a>
                <a to="/#aim" className='block'>Our aim</a>
                <Link to="/#team" className='block'>Our Team</Link>
            </div>
            <div>
                <p className='font-bold'>FAQ</p>
                <Link href="#1" className='block'>How to get the forecast of my state?</Link>
                <Link href="#1" className='block'>Are these forecasts act as real-time ground-level current air quality?</Link>
                <Link href="#1" className='block'></Link>
            </div>
        </div>
    );
};

export default Footer;
